--
-- 表的结构 `user`
--

CREATE TABLE `user` (
  `id` int(11) NOT NULL COMMENT '主键ID,自动增加',
  `targetid` text COMMENT '关注人id',
  `fansid` text COMMENT '粉丝id',
  `username` char(32) DEFAULT NULL COMMENT '用户昵称',
  `account` char(32) NOT NULL COMMENT '注册帐号',
  `passwd` char(32) NOT NULL COMMENT '登录密码,md5',
  `tel` char(11) DEFAULT NULL COMMENT '手机号码',
  `loginnum` int(11) NOT NULL DEFAULT '0' COMMENT '登录次数',
  `blognum` int(11) DEFAULT '0' COMMENT '发布博客数',
  `lasttime` datetime DEFAULT NULL COMMENT '最后登录时间',
  `sex` char(11) DEFAULT NULL COMMENT '性别',
  `img` char(240) DEFAULT NULL COMMENT '头像图路径',
  `sign` text COMMENT '个性签名',
  `addtime` datetime NOT NULL COMMENT '帐号注册时间',
  `blacklist` int(11) NOT NULL DEFAULT '0' COMMENT '黑名单',
  `email` char(240) DEFAULT NULL COMMENT '邮箱',
  `ad` char(5) NOT NULL DEFAULT 'f' COMMENT '是否发布广告栏'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='用户信息表';

